import axios from "axios";

const apiURL = "http://localhost:8000";

export const isAuthenticate = () =>
  localStorage.getItem("jwt") ? JSON.parse(localStorage.getItem("jwt")) : false;

export const isAdmin = () =>
  localStorage.getItem("jwt")
    ? JSON.parse(localStorage.getItem("jwt")).user.role === 1
    : false;

export const loginReq = async ({ email, password }) => {
  const data = { email, password };
  try {
    let res = await axios.post(`${apiURL}/api/signin`, data);
    return res.data;
  } catch (error) {
    console.log(error);
    return { error: "Login request failed" };
  }
};

export const signupReq = async ({ name, email, password, cPassword }) => {
  const data = { name, email, password, cPassword };
  try {
    let res = await axios.post(`${apiURL}/api/signup`, data);
    return res.data;
  } catch (error) {
    console.log(error);
    return { error: "Signup request failed" };
  }
};

// **Add this function to handle OTP verification**
export const verifyOtpReq = async ({ email, otp }) => {
  const data = { email, otp };
  try {
    let res = await axios.post(`${apiURL}/api/verify-otp`, data);
    return res.data;
  } catch (error) {
    console.log(error);
    return { error: "OTP verification failed" };
  }
};
